mn !important;<!-- [et_pb_line_break_holder] -->  transition: visibility 0.3s, opacity 0.3s ease-in-out; /* Animate the menu to fade in */<!-- [et_pb_line_break_holder] -->  padding: 0 !important;<!-- [et_pb_line_break_holder] --> <!-- [et_pb_line_break_holder] -->}<!-- [et_pb_line_break_holder] --><!-- [et_pb_line_break_holder] -->/* Show fullscreen menu */<!-- [et_pb_line_break_holder] --><!-- [et_pb_line_break_holder] -->.de-burger-menu .opened #mobile_menu1 {<!-- [et_pb_line_break_holder] -->  opacity: 1 !important; /* Make it visible by setting opacity to 1 */<!-- [et_pb_line_break_holder] -->  visibility: visible !important; /* Show the menu */<!-- [et_pb_line_break_holder] -->}<!-- [et_pb_line_break_holder] --><!-- [et_pb_line_break_holder] -->/* Hide and fade out the Menu when closed */<!-- [et_pb_line_break_holder] --><!-- [et_pb_line_break_holder] -->.de-burger-menu .closed #mobile_menu1 {<!-- [et_pb_line_break_holder] -->  opacity: 0 !important; /* Make it invisible by setting opacity to 0 */<!-- [et_pb_line_break_holder] -->  visibility: hidden !important; /* Hide the menu */<!-- [et_pb_line_break_holder] -->  transition: visibility 0.3s, opacity 0.3s, left 1s, ease-in-out !important; /* Animate the menu to fade out */<!-- [et_pb_line_break_holder] -->}<!-- [et_pb_line_break_holder] --><!-- [et_pb_line_break_holder] -->/* Remove Bullets next to LI Elements */<!-- [et_pb_line_break_holder] --><!-- [et_pb_line_break_holder] -->.de-burger-menu #mobile_menu1 li {<!-- [et_pb_line_break_holder] -->  list-style: none !important;<!-- [et_pb_line_break_holder] -->  text-align: center !important;<!-- [et_pb_line_break_holder] -->  width: 100%<!-- [et_pb_line_break_holder] -->}<!-- [et_pb_line_break_holder] --><!-- [et_pb_line_break_holder] -->/* Make sure that the menu is above other elements */<!-- [et_pb_line_break_holder] --><!-- [et_pb_line_break_holder] -->.de-burger-menu .et_pb_menu__wrap span.mobile_menu_bar {<!-- [et_pb_line_break_holder] -->  z-index: 999999 !important;<!-- [et_pb_line_break_holder] -->}<!-- [et_pb_line_break_holder] --><!-- [et_pb_line_break_holder] -->/* Set the close icon for when the menu is open */<!-- [et_pb_line_break_holder] --><!-- [et_pb_line_break_holder] -->.de-burger-menu .et_pb_menu__wrap .opened .mobile_menu_bar:before {<!-- [et_pb_line_break_holder] -->  color: white !important; /* Icon color */<!-- [et_pb_line_break_holder] -->  content: "\4d" !important; /* Divi font icon to use for the close icon */<!-- [et_pb_line_break_holder] -->  left: -40px; /* Close icon position. You might need to play with this to make it look right */<!-- [et_pb_line_break_holder] -->}<!-- [et_pb_line_break_holder] --><!-- [et_pb_line_break_holder] -->/* Keep hamburger icon in fixed position on mobile */<!-- [et_pb_line_break_holder] -->.de-burger-menu .opened .mobile_menu_bar {<!-- [et_pb_line_break_holder] -->  position: fixed !important;<!-- [et_pb_line_break_holder] -->}<!-- [et_pb_line_break_holder] --><!-- [et_pb_line_break_holder] -->/* Remove mobile menu border */<!-- [et_pb_line_break_holder] --><!-- [et_pb_line_break_holder] -->.de-burger-menu .et_mobile_menu {<!-- [et_pb_line_break_holder] -->  border-top: none;<!-- [et_pb_line_break_holder] -->}<!-- [et_pb_line_break_holder] --><!-- [et_pb_line_break_holder] -->/* Make sure the menu items do not show a background */<!-- [et_pb_line_break_holder] --><!-- [et_pb_line_break_holder] -->.de-burger-menu .et_mobile_menu .menu-item-has-children>a {<!-- [et_pb_line_break_holder] -->  background-color: transparent;<!-- [et_pb_line_break_holder] -->}<!-- [et_pb_line_break_holder] --><!-- [et_pb_line_break_holder] -->/* Remove the hover background from the menu items and add hover animation */<!-- [et_pb_line_break_holder] --><!-- [et_pb_line_break_holder] -->.et_mobile_menu li a:hover {<!-- [et_pb_line_break_holder] -->  background-color: transparent;<!-- [et_pb_line_break_holder] -->  opacity: 1;<!-- [et_pb_line_break_holder] -->  transition: transform 