der_layout"]<script><!-- [et_pb_line_break_holder] -->jQuery(document).ready(function($) { //When the page is loaded<!-- [et_pb_line_break_holder] -->  $('.mobile_menu_bar').click(function() { //Do something when hamburger icon is clicked<!-- [et_pb_line_break_holder] -->    $('.de-menu-bg-container').toggleClass('de-menu-bg-show'); //Toggle the class that shows/hides menu background<!-- [et_pb_line_break_holder] -->  });<!-- [et_pb_line_break_holder] --><!-- [et_pb_line_break_holder] -->  $(document).on("mouseenter mouseleave", ".menu-item", function(e) { //When the mouse enters or leaves a menu item container<!-- [et_pb_line_break_holder] -->    if (e.type == "mouseenter") { //Do something when it enters<!-- [et_pb_line_break_holder] -->      var linktext = $(this).find('a').html(), //Find the menu item that is being hovered and assign to variable<!-- [et_pb_line_break_holder] -->        content_to_change = $('.de-menu-bg-text p'); //Tell it what content needs to be changed which is out menu background text<!-- [et_pb_line_break_holder] -->      content_to_change.html(linktext); //Change the text<!-- [et_pb_line_break_holder] -->    } else { //If the mouse is not entering the container, it is leaving, so do this<!-- [et_pb_line_break_holder] -->      $('.de-menu-bg-text p').html(''); //Clear the menu background text<!-- [et_pb_line_break_holder] -->    }<!-- [et_pb_line_break_holder] -->  });<!-- [et_pb_line_break_holder] -->});<!-- [et_pb_line_break_holder] -->  <!-- [et_pb_line_break_holder] --></script>[/et_pb_code][/et_pb_column][/et_pb_row][et_pb_row module_class="de-menu-bg-container" _builder_version="4.18.0" _module_preset="default" background_color="#232427" width="100%" max_width="2560px" custom_margin="0px|0px||0px|false|false" custom_padding="0px|0px|0px|0px|false|false" global_colors_info="{}" theme_builder_area="et_header_layout"][et_pb_column type="4_4" _builder_version="4.18.0" _module_preset="default" global_colors_info="{}" theme_builder_area="et